static void Main(string[] args){

    switch ('A')
    {
        case 'A':
            Console.WriteLine( "it's A" );
            break;
        case 'B':
            Console.WriteLine( "it's B" );
            break;
        default:
            Console.WriteLine( "It's neither A nor B" );
            break;
    }
}

/* Static because we only want one instance of this Method (no objects)*/
static void func(string name){
    Console.WriteLine( "your name is " + name );
}

/* a and b are passed by value objects would be passed by reference */
static int add(int a, int b){
    return a + b; //gives this back to the Method caller
}


static void Main(string[] args){
    func("tom"); //prints your name is tom

    add(4,5); // returns 9

    func(add(5,6)); // prints your name is 11
}

//Declaring static arrays

type[] name; //where type is the objects type and name is the variable name

//example
int[] myIntArray;

//static arrays can't be changed during runtime (static size)
double[] money = new double[MAX_ARRAY_SIZE];

//arrays are pointers to the memory location and indexing an array is equal to array pointer + (index * sizeof(object));
