public struct CoOrds
{
    public int x, y; // public variables

    public CoOrds(int p1, int p2)//struct constructor
    {
        x = p1;
        y = p2;
    }

    //struct based method
    public int multiply(int p1, int p2){
        return p1 + p2;
    }
}


//create a struct
CoOrds house = new CoOrds();

private class CoOrds
{
    private int x, y; // public variables

    private CoOrds(int p1, int p2)//class constructor
    {
        x = p1;
        y = p2;
    }

    //class based method
    private int multiply(int p1, int p2){
        return p1 + p2;
    }
}


//create a class
CoOrds house = new CoOrds();
